import React, { useState, useEffect } from 'react';
import { useSuiClient, useCurrentAccount } from '@mysten/dapp-kit';
import CampaignCard from './components/CampaignCard';
import CreateCampaignForm from './components/CreateCampaignForm';
import { fetchAllCampaigns, Campaign } from './utils/campaignManager';

function App() {
  const [campaigns, setCampaigns] = useState<Campaign[]>([]);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const suiClient = useSuiClient();
  const currentAccount = useCurrentAccount();

  // Load campaigns t·ª´ blockchain
  const loadCampaigns = async () => {
    try {
      console.log('üîÑ Loading campaigns from blockchain...');
      const data = await fetchAllCampaigns(suiClient);
      console.log('‚úÖ Loaded campaigns:', data);
      
      setCampaigns(prevCampaigns => {
        // L·∫•y campaigns t·ª´ blockchain (c√≥ objectId th·∫≠t)
        const blockchainCampaigns = data;
        
        // L·∫•y campaigns optimistic (ch∆∞a c√≥ tr√™n blockchain)
        // Optimistic campaign c√≥ ID l√† txDigest (66 chars b·∫Øt ƒë·∫ßu v·ªõi 0x)
        // Blockchain campaign c√≥ ID l√† objectId (ng·∫Øn h∆°n, ~66 chars)
        const optimisticCampaigns = prevCampaigns.filter(camp => {
          // N·∫øu campaign ch∆∞a c√≥ trong blockchain data, gi·ªØ l·∫°i
          const existsInBlockchain = blockchainCampaigns.some(bc => 
            bc.title === camp.title && 
            bc.creator === camp.creator &&
            Math.abs(bc.createdAt - camp.createdAt) < 60000 // T·∫°o trong v√≤ng 1 ph√∫t
          );
          return !existsInBlockchain;
        });

        console.log('üìä Blockchain campaigns:', blockchainCampaigns.length);
        console.log('‚è≥ Optimistic campaigns:', optimisticCampaigns.length);

        // Merge: ∆Øu ti√™n blockchain campaigns, th√™m optimistic n·∫øu ch∆∞a c√≥
        const merged = [...blockchainCampaigns, ...optimisticCampaigns];
        
        // Sort by createdAt (m·ªõi nh·∫•t tr∆∞·ªõc)
        return merged.sort((a, b) => b.createdAt - a.createdAt);
      });
      
      setLoading(false);
    } catch (error) {
      console.error('‚ùå Error loading campaigns:', error);
      setLoading(false);
    }
  };

  // Load khi mount
  useEffect(() => {
    loadCampaigns();
  }, []);

  // Handle khi t·∫°o campaign th√†nh c√¥ng
  const handleCampaignCreated = (newCampaign: Campaign) => {
    console.log('‚úÖ Campaign created!', newCampaign);
    
    // B∆∞·ªõc 1: Th√™m campaign v√†o danh s√°ch NGAY L·∫¨P T·ª®C (optimistic update)
    setCampaigns(prev => [newCampaign, ...prev]);
    
    // B∆∞·ªõc 2: ƒê√≥ng form
    setShowCreateForm(false);
    
    // B∆∞·ªõc 3: Th√¥ng b√°o
    alert(`‚úÖ Campaign "${newCampaign.title}" created successfully!`);
    
    // B∆∞·ªõc 4: Scroll xu·ªëng ƒë·ªÉ xem campaign
    setTimeout(() => {
      window.scrollTo({ top: 400, behavior: 'smooth' });
    }, 500);
    
    // B∆∞·ªõc 5: Auto-refresh ƒë·ªÉ l·∫•y data th·∫≠t t·ª´ blockchain
    // Retry logic: Th·ª≠ 6 l·∫ßn, m·ªói 5 gi√¢y
    let attempts = 0;
    const maxAttempts = 6;
    
    const tryRefresh = () => {
      attempts++;
      console.log(`üîÑ Auto-refresh attempt ${attempts}/${maxAttempts}...`);
      
      loadCampaigns().then(() => {
        // N·∫øu ch∆∞a t√¨m th·∫•y campaign t·ª´ blockchain v√† c√≤n attempts, th·ª≠ l·∫°i
        if (attempts < maxAttempts) {
          setTimeout(tryRefresh, 5000);
        }
      });
    };
    
    // B·∫Øt ƒë·∫ßu sau 5 gi√¢y (cho blockchain k·ªãp index)
    setTimeout(tryRefresh, 5000);
  };

  // Handle khi donate th√†nh c√¥ng
  const handleDonationSuccess = () => {
    console.log('‚úÖ Donation successful, refreshing campaigns...');
    loadCampaigns();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-blue-50">
      {/* Header */}
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 py-6">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">
                üíù Charity DApp
              </h1>
              <p className="text-gray-600 mt-1">
                Create and support charitable campaigns on Sui blockchain
              </p>
            </div>
            
            {currentAccount ? (
              <div className="text-sm text-gray-600">
                Connected: {currentAccount.address.slice(0, 6)}...{currentAccount.address.slice(-4)}
              </div>
            ) : (
              <div className="text-sm text-red-600">
                ‚ö†Ô∏è Please connect wallet
              </div>
            )}
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 py-8">
        {/* Create Campaign Button */}
        {!showCreateForm && (
          <div className="mb-8 text-center">
            <button
              onClick={() => setShowCreateForm(true)}
              className="bg-gradient-to-r from-purple-600 to-blue-600 text-white px-8 py-3 rounded-lg font-semibold hover:from-purple-700 hover:to-blue-700 transition-all shadow-lg hover:shadow-xl"
            >
              ‚ûï Create New Campaign
            </button>
          </div>
        )}

        {/* Create Campaign Form */}
        {showCreateForm && (
          <div className="mb-8">
            <div className="bg-white rounded-xl shadow-lg p-6">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-2xl font-bold">Create New Campaign</h2>
                <button
                  onClick={() => setShowCreateForm(false)}
                  className="text-gray-500 hover:text-gray-700"
                >
                  ‚úï Close
                </button>
              </div>
              <CreateCampaignForm onSuccess={handleCampaignCreated} />
            </div>
          </div>
        )}

        {/* Campaigns List */}
        <div>
          <h2 className="text-2xl font-bold mb-6">
            {loading ? 'Loading Campaigns...' : `All Campaigns (${campaigns.length})`}
          </h2>
          
          {loading ? (
            <div className="text-center py-12">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600 mx-auto"></div>
              <p className="mt-4 text-gray-600">Loading campaigns...</p>
            </div>
          ) : campaigns.length === 0 ? (
            <div className="text-center py-12 bg-white rounded-xl shadow">
              <p className="text-gray-600 text-lg">No campaigns yet.</p>
              <p className="text-gray-500 mt-2">Be the first to create one!</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {campaigns.map((campaign) => (
                <CampaignCard
                  key={campaign.id}
                  campaign={campaign}
                  onDonationSuccess={handleDonationSuccess}
                />
              ))}
            </div>
          )}
        </div>

        {/* Footer Note */}
        <div className="mt-12 text-center text-sm text-gray-500">
          <p>üí° Tip: Campaigns marked with "‚è≥ Pending" are waiting for blockchain confirmation.</p>
          <p>Once confirmed, you'll be able to donate!</p>
        </div>
      </main>
    </div>
  );
}

export default App;
