import React, { useState, useEffect } from 'react';
import { useSuiClient, useCurrentAccount } from '@mysten/dapp-kit';
import { CampaignCard } from './components/CampaignCard';
import { CreateCampaignForm } from './components/CreateCampaignForm';
import { fetchAllCampaigns, Campaign } from './utils/campaignManager';

function App() {
  const [campaigns, setCampaigns] = useState<Campaign[]>([]);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const suiClient = useSuiClient();
  const currentAccount = useCurrentAccount();

  // Load campaigns t·ª´ blockchain
  const loadCampaigns = async () => {
    try {
      console.log('üîÑ Loading campaigns from blockchain...');
      const data = await fetchAllCampaigns(suiClient);
      console.log('‚úÖ Loaded campaigns:', data);
      
      // IMPORTANT: Merge v·ªõi campaigns hi·ªán t·∫°i, gi·ªØ optimistic campaigns
      setCampaigns(prevCampaigns => {
        // Campaigns t·ª´ blockchain (c√≥ object ID th·∫≠t)
        const blockchainCampaigns = data;
        
        // Campaigns optimistic (ch∆∞a c√≥ tr√™n blockchain)
        // Optimistic campaign c√≥ ID = txDigest (66 chars, format: 0xABC...123)
        // Blockchain campaign c√≥ ID = objectId (66 chars, format: 0x123...456)
        // C√°ch ph√¢n bi·ªát: Check xem c√≥ trong blockchain data kh√¥ng
        const optimisticCampaigns = prevCampaigns.filter(camp => {
          // N·∫øu campaign match v·ªõi blockchain campaign (same title, creator, goal)
          // th√¨ kh√¥ng ph·∫£i optimistic n·ªØa
          const existsInBlockchain = blockchainCampaigns.some(bc => 
            bc.title === camp.title && 
            bc.creator === camp.creator &&
            Math.abs(bc.createdAt - camp.createdAt) < 120000 // Within 2 minutes
          );
          return !existsInBlockchain;
        });

        console.log('üìä Stats:');
        console.log('  - Blockchain campaigns:', blockchainCampaigns.length);
        console.log('  - Optimistic campaigns:', optimisticCampaigns.length);

        // Merge: blockchain campaigns + optimistic campaigns
        const merged = [...blockchainCampaigns, ...optimisticCampaigns];
        
        // Sort by createdAt (newest first)
        return merged.sort((a, b) => b.createdAt - a.createdAt);
      });
      
      setLoading(false);
    } catch (error) {
      console.error('‚ùå Error loading campaigns:', error);
      setLoading(false);
    }
  };

  // Load campaigns khi mount
  useEffect(() => {
    loadCampaigns();
  }, []);

  // Handle khi t·∫°o campaign th√†nh c√¥ng
  const handleCampaignCreated = (newCampaign: Campaign) => {
    console.log('‚úÖ Campaign created:', newCampaign);
    
    // B∆∞·ªõc 1: Add campaign NGAY L·∫¨P T·ª®C (optimistic update)
    setCampaigns(prev => [newCampaign, ...prev]);
    
    // B∆∞·ªõc 2: ƒê√≥ng form
    setShowCreateForm(false);
    
    // B∆∞·ªõc 3: Th√¥ng b√°o
    alert(`‚úÖ Campaign "${newCampaign.title}" created successfully! üéâ`);
    
    // B∆∞·ªõc 4: Scroll xu·ªëng ƒë·ªÉ xem campaign
    setTimeout(() => {
      window.scrollTo({ 
        top: 500, 
        behavior: 'smooth' 
      });
    }, 500);
    
    // B∆∞·ªõc 5: Auto-refresh ƒë·ªÉ l·∫•y data th·∫≠t t·ª´ blockchain
    // Retry logic: Th·ª≠ 10 l·∫ßn, m·ªói 4 gi√¢y
    let attempts = 0;
    const maxAttempts = 10;
    
    const tryRefresh = async () => {
      attempts++;
      console.log(`üîÑ Auto-refresh attempt ${attempts}/${maxAttempts}...`);
      
      await loadCampaigns();
      
      // N·∫øu ch∆∞a t√¨m th·∫•y campaign t·ª´ blockchain, th·ª≠ l·∫°i
      if (attempts < maxAttempts) {
        setTimeout(tryRefresh, 4000); // 4 seconds
      } else {
        console.log('‚è±Ô∏è Max refresh attempts reached. Campaign will remain in optimistic state.');
      }
    };
    
    // B·∫Øt ƒë·∫ßu sau 5 gi√¢y (cho blockchain k·ªãp index)
    setTimeout(tryRefresh, 5000);
  };

  // Handle khi donate th√†nh c√¥ng
  const handleDonationSuccess = () => {
    console.log('‚úÖ Donation successful, refreshing campaigns...');
    loadCampaigns();
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      paddingBottom: '40px',
    }}>
      {/* Header */}
      <header style={{
        backgroundColor: 'rgba(255,255,255,0.95)',
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
        padding: '20px 0',
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '0 20px',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
        }}>
          <div>
            <h1 style={{
              margin: 0,
              fontSize: '32px',
              fontWeight: '800',
              background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
            }}>
              üíù Charity DApp
            </h1>
            <p style={{
              margin: '4px 0 0 0',
              color: '#616161',
              fontSize: '14px',
            }}>
              Decentralized charity platform on Sui blockchain
            </p>
          </div>
          
          {currentAccount ? (
            <div style={{
              padding: '10px 20px',
              backgroundColor: '#e8f5e9',
              borderRadius: '12px',
              border: '2px solid #4caf50',
            }}>
              <div style={{
                fontSize: '12px',
                color: '#2e7d32',
                fontWeight: '600',
              }}>
                üü¢ Connected
              </div>
              <div style={{
                fontSize: '13px',
                color: '#616161',
                fontFamily: 'monospace',
                marginTop: '4px',
              }}>
                {currentAccount.address.slice(0, 8)}...{currentAccount.address.slice(-6)}
              </div>
            </div>
          ) : (
            <div style={{
              padding: '10px 20px',
              backgroundColor: '#ffebee',
              borderRadius: '12px',
              border: '2px solid #f44336',
              color: '#c62828',
              fontWeight: '600',
            }}>
              ‚ö†Ô∏è Wallet not connected
            </div>
          )}
        </div>
      </header>

      {/* Main Content */}
      <main style={{
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '40px 20px',
      }}>
        {/* Create Campaign Button/Form */}
        {!showCreateForm ? (
          <div style={{
            textAlign: 'center',
            marginBottom: '40px',
          }}>
            <button
              onClick={() => setShowCreateForm(true)}
              disabled={!currentAccount}
              style={{
                padding: '16px 40px',
                fontSize: '18px',
                fontWeight: '700',
                color: '#fff',
                background: currentAccount 
                  ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
                  : '#bdbdbd',
                border: 'none',
                borderRadius: '12px',
                cursor: currentAccount ? 'pointer' : 'not-allowed',
                boxShadow: currentAccount ? '0 4px 16px rgba(102, 126, 234, 0.4)' : 'none',
                transition: 'all 0.3s ease',
              }}
              onMouseEnter={(e) => {
                if (currentAccount) {
                  e.currentTarget.style.transform = 'translateY(-2px)';
                  e.currentTarget.style.boxShadow = '0 6px 20px rgba(102, 126, 234, 0.5)';
                }
              }}
              onMouseLeave={(e) => {
                if (currentAccount) {
                  e.currentTarget.style.transform = 'translateY(0)';
                  e.currentTarget.style.boxShadow = '0 4px 16px rgba(102, 126, 234, 0.4)';
                }
              }}
            >
              ‚ûï Create New Campaign
            </button>
            {!currentAccount && (
              <p style={{
                marginTop: '12px',
                color: 'rgba(255,255,255,0.9)',
                fontSize: '14px',
              }}>
                Please connect your wallet to create a campaign
              </p>
            )}
          </div>
        ) : (
          <div style={{ marginBottom: '40px' }}>
            <CreateCampaignForm
              onSuccess={handleCampaignCreated}
              onCancel={() => setShowCreateForm(false)}
            />
          </div>
        )}

        {/* Campaigns Section */}
        <div>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '24px',
          }}>
            <h2 style={{
              margin: 0,
              fontSize: '28px',
              fontWeight: '800',
              color: '#fff',
            }}>
              {loading ? 'Loading Campaigns...' : `All Campaigns (${campaigns.length})`}
            </h2>
            
            {!loading && campaigns.length > 0 && (
              <button
                onClick={loadCampaigns}
                style={{
                  padding: '10px 20px',
                  backgroundColor: 'rgba(255,255,255,0.2)',
                  border: '2px solid rgba(255,255,255,0.4)',
                  borderRadius: '8px',
                  color: '#fff',
                  fontWeight: '600',
                  cursor: 'pointer',
                  transition: 'all 0.2s',
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.backgroundColor = 'rgba(255,255,255,0.3)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.backgroundColor = 'rgba(255,255,255,0.2)';
                }}
              >
                üîÑ Refresh
              </button>
            )}
          </div>
          
          {loading ? (
            <div style={{
              textAlign: 'center',
              padding: '60px 20px',
              backgroundColor: 'rgba(255,255,255,0.95)',
              borderRadius: '16px',
            }}>
              <div style={{
                display: 'inline-block',
                width: '48px',
                height: '48px',
                border: '4px solid #e0e0e0',
                borderTop: '4px solid #667eea',
                borderRadius: '50%',
                animation: 'spin 1s linear infinite',
              }} />
              <p style={{
                marginTop: '20px',
                color: '#616161',
                fontSize: '16px',
              }}>
                Loading campaigns from blockchain...
              </p>
              <style>
                {`
                  @keyframes spin {
                    0% { transform: rotate(0deg); }
                    100% { transform: rotate(360deg); }
                  }
                `}
              </style>
            </div>
          ) : campaigns.length === 0 ? (
            <div style={{
              textAlign: 'center',
              padding: '60px 20px',
              backgroundColor: 'rgba(255,255,255,0.95)',
              borderRadius: '16px',
            }}>
              <div style={{ fontSize: '64px', marginBottom: '20px' }}>üì≠</div>
              <h3 style={{
                margin: '0 0 12px 0',
                fontSize: '24px',
                color: '#212121',
              }}>
                No campaigns yet
              </h3>
              <p style={{
                margin: 0,
                color: '#757575',
                fontSize: '16px',
              }}>
                Be the first to create a charitable campaign!
              </p>
            </div>
          ) : (
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fill, minmax(320px, 1fr))',
              gap: '24px',
            }}>
              {campaigns.map((campaign) => (
                <CampaignCard
                  key={campaign.id}
                  campaign={campaign}
                  onDonationSuccess={handleDonationSuccess}
                />
              ))}
            </div>
          )}
        </div>

        {/* Info Footer */}
        {campaigns.some(c => c.id.length > 66 || !c.id.startsWith('0x')) && (
          <div style={{
            marginTop: '40px',
            padding: '20px',
            backgroundColor: 'rgba(255, 255, 255, 0.1)',
            borderRadius: '12px',
            border: '2px solid rgba(255, 255, 255, 0.2)',
            textAlign: 'center',
          }}>
            <p style={{
              margin: 0,
              color: 'rgba(255, 255, 255, 0.9)',
              fontSize: '14px',
              lineHeight: '1.6',
            }}>
              üí° <strong>Tip:</strong> Campaigns marked with "‚è≥ Pending" are waiting for blockchain confirmation.
              <br />
              This usually takes 10-20 seconds. Once confirmed, you'll be able to donate!
            </p>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
