import React, { useState } from 'react';
import { Campaign } from '../utils/campaignManager';
import DonateModal from './DonateModal';

interface Props {
  campaign: Campaign;
  onDonationSuccess: () => void;
}

const CampaignCard: React.FC<Props> = ({ campaign, onDonationSuccess }) => {
  const [showDonateModal, setShowDonateModal] = useState(false);

  // T√≠nh progress
  const progress = campaign.goal > 0 
    ? Math.min((campaign.raised / campaign.goal) * 100, 100) 
    : 0;

  // Check campaign status
  const now = Date.now();
  const isExpired = campaign.deadline < now;
  const isCompleted = campaign.raised >= campaign.goal;
  const isActive = !isExpired && !isCompleted;

  // Detect optimistic (pending) campaign
  // Optimistic campaign c√≥ ID l√† txDigest (66 chars b·∫Øt ƒë·∫ßu 0x)
  // Blockchain campaign c√≥ ID l√† objectId (c≈©ng ~66 chars NH∆ØNG kh√¥ng b·∫Øt ƒë·∫ßu v·ªõi 0x0000)
  // C√°ch t·ªët nh·∫•t: Check xem c√≥ th·ªÉ query ƒë∆∞·ª£c object kh√¥ng
  const isPending = !campaign.id.startsWith('0x') || campaign.id.length > 66 || campaign.id.includes('000000000');

  // Status badge
  const getStatusBadge = () => {
    if (isPending) {
      return <span className="px-3 py-1 bg-yellow-100 text-yellow-800 text-xs font-semibold rounded-full">‚è≥ Pending Confirmation</span>;
    }
    if (isCompleted) {
      return <span className="px-3 py-1 bg-green-100 text-green-800 text-xs font-semibold rounded-full">‚úÖ Funded</span>;
    }
    if (isExpired) {
      return <span className="px-3 py-1 bg-red-100 text-red-800 text-xs font-semibold rounded-full">‚è∞ Expired</span>;
    }
    return <span className="px-3 py-1 bg-blue-100 text-blue-800 text-xs font-semibold rounded-full">üî• Active</span>;
  };

  // Format address
  const formatAddress = (addr: string) => {
    return `${addr.slice(0, 6)}...${addr.slice(-4)}`;
  };

  // Format SUI amount
  const formatSUI = (amount: number) => {
    return (amount / 1_000_000_000).toFixed(2);
  };

  // Format time remaining
  const getTimeRemaining = () => {
    if (isExpired) return 'Expired';
    
    const diff = campaign.deadline - now;
    const days = Math.floor(diff / (1000 * 60 * 60 * 24));
    const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    
    if (days > 0) return `${days} days left`;
    if (hours > 0) return `${hours} hours left`;
    return 'Less than 1 hour';
  };

  return (
    <>
      <div className="bg-white rounded-xl shadow-md hover:shadow-xl transition-all duration-300 overflow-hidden">
        {/* Campaign Image/Header */}
        <div className="bg-gradient-to-r from-purple-500 to-blue-500 h-32 flex items-center justify-center">
          <span className="text-6xl">üíù</span>
        </div>

        {/* Campaign Content */}
        <div className="p-6">
          {/* Status Badge */}
          <div className="mb-3">
            {getStatusBadge()}
          </div>

          {/* Title */}
          <h3 className="text-xl font-bold text-gray-900 mb-2 line-clamp-2">
            {campaign.title}
          </h3>

          {/* Description */}
          <p className="text-gray-600 text-sm mb-4 line-clamp-3">
            {campaign.description}
          </p>

          {/* Creator */}
          <div className="text-xs text-gray-500 mb-4">
            By: {formatAddress(campaign.creator)}
          </div>

          {/* Progress Bar */}
          <div className="mb-4">
            <div className="flex justify-between text-sm mb-1">
              <span className="text-gray-600">Progress</span>
              <span className="font-semibold text-purple-600">{progress.toFixed(1)}%</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div
                className="bg-gradient-to-r from-purple-600 to-blue-600 h-2 rounded-full transition-all duration-500"
                style={{ width: `${progress}%` }}
              />
            </div>
          </div>

          {/* Stats */}
          <div className="grid grid-cols-2 gap-4 mb-4 text-sm">
            <div>
              <div className="text-gray-500">Goal</div>
              <div className="font-bold text-gray-900">{formatSUI(campaign.goal)} SUI</div>
            </div>
            <div>
              <div className="text-gray-500">Raised</div>
              <div className="font-bold text-green-600">{formatSUI(campaign.raised)} SUI</div>
            </div>
            <div className="col-span-2">
              <div className="text-gray-500">Remaining</div>
              <div className="font-bold text-purple-600">{formatSUI(campaign.goal - campaign.raised)} SUI</div>
            </div>
          </div>

          {/* Time Remaining */}
          <div className="text-sm text-gray-600 mb-4">
            ‚è∞ {getTimeRemaining()}
          </div>

          {/* Action Buttons */}
          <div className="flex gap-2">
            {isPending ? (
              <button
                disabled
                className="flex-1 bg-gray-300 text-gray-500 py-2 px-4 rounded-lg font-semibold cursor-not-allowed"
              >
                ‚è≥ Waiting for Confirmation...
              </button>
            ) : isActive ? (
              <button
                onClick={() => setShowDonateModal(true)}
                className="flex-1 bg-gradient-to-r from-purple-600 to-blue-600 text-white py-2 px-4 rounded-lg font-semibold hover:from-purple-700 hover:to-blue-700 transition-all shadow-md hover:shadow-lg"
              >
                üí∞ Donate Now
              </button>
            ) : (
              <button
                disabled
                className="flex-1 bg-gray-300 text-gray-500 py-2 px-4 rounded-lg font-semibold cursor-not-allowed"
              >
                {isExpired ? '‚è∞ Expired' : '‚úÖ Funded'}
              </button>
            )}

            {!isPending && (
              <a
                href={`https://suiscan.xyz/testnet/object/${campaign.id}`}
                target="_blank"
                rel="noopener noreferrer"
                className="bg-gray-200 hover:bg-gray-300 text-gray-700 py-2 px-4 rounded-lg font-semibold transition-all"
              >
                üîç
              </a>
            )}
          </div>

          {/* Debug Info (Remove in production) */}
          {process.env.NODE_ENV === 'development' && (
            <div className="mt-4 p-2 bg-gray-100 rounded text-xs">
              <div>ID: {campaign.id.slice(0, 20)}...</div>
              <div>Pending: {isPending ? 'Yes' : 'No'}</div>
              <div>ID Length: {campaign.id.length}</div>
            </div>
          )}
        </div>
      </div>

      {/* Donate Modal */}
      {showDonateModal && !isPending && (
        <DonateModal
          campaign={campaign}
          onClose={() => setShowDonateModal(false)}
          onSuccess={() => {
            setShowDonateModal(false);
            onDonationSuccess();
          }}
        />
      )}
    </>
  );
};

export default CampaignCard;
