import { SuiClient } from '@mysten/sui/client';
import { Transaction } from '@mysten/sui/transactions';
import { PACKAGE_ID, MODULE_NAME } from '../config/constants';

export interface Campaign {
  id: string;
  title: string;
  description: string;
  creator: string;
  beneficiary: string;
  goal: number;
  raised: number;
  deadline: number;
  createdAt: number;
}

/**
 * Fetch all campaigns from Sui blockchain
 * Method 1: Query shared objects c·ªßa type Campaign
 */
export async function fetchAllCampaigns(suiClient: SuiClient): Promise<Campaign[]> {
  try {
    console.log('üîç Querying campaigns from Sui blockchain...');
    console.log('üì¶ Package ID:', PACKAGE_ID);
    console.log('üìù Module Name:', MODULE_NAME);

    // Query t·∫•t c·∫£ shared objects c√≥ type Campaign
    const campaignType = `${PACKAGE_ID}::${MODULE_NAME}::Campaign`;
    console.log('üéØ Querying type:', campaignType);

    const response = await suiClient.getOwnedObjects({
      owner: PACKAGE_ID,
      filter: {
        StructType: campaignType,
      },
      options: {
        showContent: true,
        showType: true,
      },
    });

    console.log('üìä Query response:', response);

    if (!response.data || response.data.length === 0) {
      console.log('‚ö†Ô∏è No campaigns found with getOwnedObjects, trying queryEvents...');
      return await fetchCampaignsFromEvents(suiClient);
    }

    // Parse campaigns t·ª´ response
    const campaigns: Campaign[] = [];
    
    for (const item of response.data) {
      try {
        const objectId = item.data?.objectId;
        if (!objectId) continue;

        // Fetch full object content
        const objectData = await suiClient.getObject({
          id: objectId,
          options: {
            showContent: true,
          },
        });

        const content = objectData.data?.content;
        if (content && 'fields' in content) {
          const fields = content.fields as any;
          
          campaigns.push({
            id: objectId,
            title: fields.title || 'Untitled',
            description: fields.description || '',
            creator: fields.creator || '',
            beneficiary: fields.beneficiary || '',
            goal: parseInt(fields.goal || '0'),
            raised: parseInt(fields.raised || '0'),
            deadline: parseInt(fields.deadline || '0'),
            createdAt: parseInt(fields.created_at || Date.now().toString()),
          });
        }
      } catch (err) {
        console.error('Error parsing campaign:', err);
      }
    }

    console.log(`‚úÖ Loaded ${campaigns.length} campaigns`);
    return campaigns;

  } catch (error) {
    console.error('‚ùå Error fetching campaigns:', error);
    return [];
  }
}

/**
 * Fetch campaigns from CampaignCreated events
 * Fallback method khi kh√¥ng query ƒë∆∞·ª£c shared objects
 */
async function fetchCampaignsFromEvents(suiClient: SuiClient): Promise<Campaign[]> {
  try {
    console.log('üîç Fetching campaigns from events...');

    const eventType = `${PACKAGE_ID}::${MODULE_NAME}::CampaignCreated`;
    console.log('üì° Event type:', eventType);

    const events = await suiClient.queryEvents({
      query: {
        MoveEventType: eventType,
      },
      limit: 50,
      order: 'descending',
    });

    console.log(`üìä Found ${events.data.length} CampaignCreated events`);

    const campaigns: Campaign[] = [];

    for (const event of events.data) {
      try {
        const fields = event.parsedJson as any;
        const campaignId = fields.campaign_id;

        // Fetch campaign object
        const objectData = await suiClient.getObject({
          id: campaignId,
          options: {
            showContent: true,
          },
        });

        const content = objectData.data?.content;
        if (content && 'fields' in content) {
          const campaignFields = content.fields as any;
          
          campaigns.push({
            id: campaignId,
            title: campaignFields.title || 'Untitled',
            description: campaignFields.description || '',
            creator: campaignFields.creator || '',
            beneficiary: campaignFields.beneficiary || '',
            goal: parseInt(campaignFields.goal || '0'),
            raised: parseInt(campaignFields.raised || '0'),
            deadline: parseInt(campaignFields.deadline || '0'),
            createdAt: parseInt(campaignFields.created_at || event.timestampMs || Date.now().toString()),
          });
        }
      } catch (err) {
        console.error('Error processing event:', err);
      }
    }

    console.log(`‚úÖ Loaded ${campaigns.length} campaigns from events`);
    return campaigns;

  } catch (error) {
    console.error('‚ùå Error fetching from events:', error);
    return [];
  }
}

/**
 * Create a new campaign
 */
export function buildCreateCampaignTransaction(
  title: string,
  description: string,
  goalAmount: number,
  deadlineMs: number,
  beneficiary: string
): Transaction {
  const tx = new Transaction();

  tx.moveCall({
    target: `${PACKAGE_ID}::${MODULE_NAME}::create_campaign`,
    arguments: [
      tx.pure.string(title),
      tx.pure.string(description),
      tx.pure.u64(goalAmount),
      tx.pure.u64(deadlineMs),
      tx.pure.address(beneficiary),
    ],
  });

  return tx;
}

/**
 * Donate to a campaign
 */
export function buildDonateTransaction(
  campaignId: string,
  amount: number
): Transaction {
  const tx = new Transaction();

  // Split coin cho donation amount
  const [coin] = tx.splitCoins(tx.gas, [amount]);

  tx.moveCall({
    target: `${PACKAGE_ID}::${MODULE_NAME}::donate`,
    arguments: [
      tx.object(campaignId),
      coin,
    ],
  });

  return tx;
}

/**
 * Withdraw funds (ch·ªâ creator/beneficiary)
 */
export function buildWithdrawTransaction(campaignId: string): Transaction {
  const tx = new Transaction();

  tx.moveCall({
    target: `${PACKAGE_ID}::${MODULE_NAME}::withdraw_funds`,
    arguments: [
      tx.object(campaignId),
    ],
  });

  return tx;
}

/**
 * Get single campaign by ID
 */
export async function getCampaignById(
  suiClient: SuiClient,
  campaignId: string
): Promise<Campaign | null> {
  try {
    const objectData = await suiClient.getObject({
      id: campaignId,
      options: {
        showContent: true,
      },
    });

    const content = objectData.data?.content;
    if (!content || !('fields' in content)) {
      return null;
    }

    const fields = content.fields as any;
    
    return {
      id: campaignId,
      title: fields.title || 'Untitled',
      description: fields.description || '',
      creator: fields.creator || '',
      beneficiary: fields.beneficiary || '',
      goal: parseInt(fields.goal || '0'),
      raised: parseInt(fields.raised || '0'),
      deadline: parseInt(fields.deadline || '0'),
      createdAt: parseInt(fields.created_at || Date.now().toString()),
    };
  } catch (error) {
    console.error('Error fetching campaign:', error);
    return null;
  }
}
